mapper.select:
SELECT * FROM jfactory_order
WHERE or_cancel_state = 0

	
		and mem_id = #{keyword}
	
	
		and or_id = #{keyword}
	
	
		and or_price = #{keyword}
	
	
		and mem_id = #{keyword}
		or or_id = #{keyword}
		or
		or_price =
		#{keyword}
	

LIMIT #{pageStart},600

